        setForm({...form, [e.target.name]: e.target.value})
    }

    const onSubmit = (e) => {
        e.preventDefault();
        console.log(form)
    }

  return (
    <form onSubmit={onSubmit}>

      <div>
        <input name="fullname" placeholder="Fullname" onChange={onChangeInput}></input>
      </div>
      <div>
        <input name="phone_number" placeholder="Phone number" onChange={onChangeInput}></input>
      </div>

      <div>
        <button>Add</button>
      </div>

    </form>
  );
}

export default Form;
