import {useState} from "react";

function Form({addContacts}) {

    const [form, setForm] = useState({fullname: "", phone_number:""});
    const onChangeInput = (e) => {
        setForm({...form, [e.target.name]: e.target.value})
    }

    const onSubmit = (e) => {
        e.preventDefault();

        if(form.fullname === '' || form.phone_number === ''){
            return false;
        }

        addContacts([form]);
        console.log(form)
    }

  return (
    <form onSubmit={onSubmit}>

      <div>
        <input name="fullname" placeholder="Fullname" onChange={onChangeInput}></input>
      </div>
      <div>
        <input name="phone_number" placeholder="Phone number" onChange={onChangeInput}></input>
      </div>

      <div>
        <button>Add</button>
      </div>

    </form>
  );
}

export default Form;
